import React, {Component, useState} from "react";
import body from './assets/body.png'; 
import mouth from './assets/mouth.png'; 

function Home() {

  // constructor(props){
  //     super(props);

  //     // on page load 
  //     this.state = {
  //         droidX: 0,
  //         mouseX: 0,
  //         toTheRight: true, 
  //         speed: 1, 
  //         accelMod: 2
  //     }
  // }
  const [droidX, setDroidX] = useState(0); 
  const [mouseX, setMouseX] = useState(0); 
  const [toTheRight, setToTheRight] = useState(true); 
  const [speed, setSpeed] = useState(1); 
  const [accelMod, setAccelMod] = useState(2); 

  // Keep track of the mouse position.
  function handleMouseMove(event) {
      setMouseX(event.pageX); 
      // this.setState({
      //     mouseX: event.pageX
      // })
  }

  function movement() {
      let {droidX, mouseX, speed, accelMod} = this.state;

      if(Math.abs(Math.round(droidX)-mouseX) !== 1){

          let distance = mouseX - droidX;
          let acceleration = Math.abs(distance * accelMod) / 100;

          // Move to the right
          if (droidX < mouseX) {
              setDroidX(droidX+(speed*acceleration)); 
              setToTheRight(true); 
              // this.setState({
              //     droidX: droidX+(speed*acceleration),
              //     toTheRight: true
              // });
          }

          // Move to the left
          else {
            setDroidX(droidX-(speed*acceleration)); 
            setToTheRight(false); 
              // this.setState({
              //     droidX: droidX-(speed*acceleration),
              //     toTheRight: false
              // });
          }
      }
  }

  // Get some initial movement on first mount. 
  function componentWillMount() {
    setMouseX(300); 
      // this.setState({
      //     mouseX: 300
      // });
  }

  // Set up the mouse event listener and fire up the movement function.
  function componentDidMount() {
      document.addEventListener('mousemove', (e) => handleMouseMove(e));
      setInterval(movement.bind(this), 1);
  }

  // Clean up.
  function componentWillUnmount() {
      document.removeEventListener('mousemove', (e) => handleMouseMove(e));
  }
  // render() {
    // let { speed, accelMod, droidX, mouseX, toTheRight} = this.state;

    return (
      <div>     

        <div className="liljess" style={{WebkitTransform: `translateX(${droidX}px)`}}>     
          <div className="body" 
               style={{WebkitTransform: `translateX(${(mouseX - droidX) / 15}px) rotateZ(${(mouseX - droidX) / 15}deg)`}}>
              <img className="img-body" src={body} alt=""></img>

              <div className={'eyes ' + (toTheRight ? 'right' : '')}>
                <div className="eye one"></div>
                <div className="eye two"></div>
                <div className="mouth">
                  <img className="img-mouth" src={mouth} alt=""></img>
                </div>
              </div>

              <div className={'pupils ' + (toTheRight ? 'right' : '')}>
                <div className="pupil one"></div>
                <div className="pupil two"></div>
              </div>

              <div className={'eyebrows ' + (toTheRight ? 'right' : '')}>

                <div className="eyebrow one"></div>
                <div className="eyebrow two"></div>
              </div>
          </div>

          <div className="scooter">
            <div className="scooter base"></div>
            <div className="scooter top" style={{WebkitTransform: `translateX(${(mouseX - droidX) / 15}px) rotateZ(${(mouseX - droidX) / 5}deg)`}}></div>
            <div className="scooter handle" style={{WebkitTransform: `translateX(${(mouseX - droidX) / 10}px) rotateZ(${(mouseX - droidX) / 5}deg)`}}></div>
          </div>

          <div className="ball-left" style={{WebkitTransform: `rotateZ(${droidX}deg)`}}>
            <div className="ring one"></div>
          </div>
          <div className="ball-right" style={{WebkitTransform: `rotateZ(${droidX}deg)`}}>
            <div className="ring one"></div>
          </div>
          <div className="shadow"></div>
        </div>

        <div className="instructions">
          <p>move your mouse.</p>
        </div>

      </div>
    )
  }
// }

export default Home;
