import React, { Component, useState } from 'react'; 

class Contact extends Component {
    constructor(props){
        super(props);

        // on page load 
        this.state = {
            name: '', 
            email: '',
            message: ''
        }
    }
    render() {

        return(
            <div>
                <h1 class="h1 contact">any questions?</h1>
                <p>Feel free to send a message below!</p>
                <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                    <div className="form-group">
                        <label for="name">Name</label>
                        <input type="text" id="name" className="form-control" />
                    </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" id="exampleInputEmail1" className="form-control" aria-describedby="emailHelp" />
                    </div>
                    <div className="form-group">
                        <label for="message">Message</label>
                        <textarea id="message" className="form-control" rows="5"></textarea>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        )                    
    }
}

export default Contact;
