import React from "react";
import { Link, Route, Switch } from "react-router-dom";
import {Provider} from '@react-spectrum/provider';
import {theme} from '@react-spectrum/theme-default';
import {Content} from '@react-spectrum/view';
import {Text} from '@react-spectrum/text';
import {Tabs, Item} from '@react-spectrum/tabs';
import {Grid, View} from '@adobe/react-spectrum'
import headshot from './assets/headshot.jpg';
import linkedin from './assets/linkedin-icon.png';
import email from './assets/email-icon.png';
import insta from './assets/insta-icon.png';
import AboutMe from './AboutMe';
import Code from './Code'; 
import Design from './Design';
import ThreeD from './ThreeD';

export default function App() {
  return (
    <Provider theme={theme} colorScheme="light">
    <div>
      <Grid
        areas={['header  header', 'sidebar content', 'footer  footer']}
        columns={['1fr', '3fr']}
        rows={['size-1000', 'auto', 'size-1000']}
        height="size-6000"
        gap="size-100">
        <View backgroundColor="celery-600" gridArea="header">
        </View> 
        <View backgroundColor="" gridArea="sidebar">
          <div class="side-panel">
            <h1>Jess Peng</h1>
            <h6>Code • Design • 3D</h6>
            <img id="headshot" src={headshot} alt="Headshot"></img><br></br>
            <p>Hi, welcome to my site!<br></br></p>
            <span>
              <img class="social-icon" src={insta} alt="Instagram"></img>
              <img class="social-icon" src={linkedin} alt="Linkedin"></img>
              <img class="social-icon" src={email} alt="Email"></img>
            </span>
          </div>
        </View> 
        <View backgroundColor="" gridArea="content">
        <Tabs aria-label="Navigation Bar">
            <Item title="About Me" key="AbMe">
              <Content marginTop="size-250" marginStart="size-125">
                <AboutMe></AboutMe>
              </Content>
            </Item>
            <Item title="Code" key="Co">
              <Content marginTop="size-250" marginStart="size-125">
                <Code></Code>
              </Content>
            </Item>
            <Item title="Design" key="Des">
              <Content marginTop="size-250" marginStart="size-125">
                <Design></Design>
              </Content>
            </Item>
          </Tabs>
        </View> 
        <View backgroundColor="magenta-600" gridArea="footer"></View> 
      </Grid>
    </div>
    </Provider>
  );
}
