import React, { Component } from 'react'; 
import headshot from './assets/headshot.jpg';
import linkedin from './assets/linkedin-icon.png';
import email from './assets/email-icon.png';
import insta from './assets/insta-icon.png';

class Sidepanel extends Component {
  state = { displayBio: false}; 

  toggleDisplayBio = () => {
      this.setState({displayBio: !this.state.displayBio}); 
  }

    render() {
        return (
            <div id="sidePanel">
                <h1>Jess Peng</h1>
                <h6>Hi, welcome to my website!</h6>
                <img id="headshot" src={headshot} alt="Headshot"></img>
                {/* { 
                    this.state.displayBio ? (
                    <div>
                        <p>I am currently a Technical Consultant at Adobe working in New York City.</p>
                        <p>I majored in <a href="http://cg.cis.upenn.edu/dmd.html">Digital Media Design</a> and minored in Fine Arts at the University of Pennsylvania, where I graduated from the School of Engineering in May 2019.</p>
                        <p>When I'm not working, I love drawing cartoons, making cards, watching stand up, and running.</p>
                        <button onClick={this.toggleDisplayBio}>Show less</button>
                    </div>)
                : (
                    <div>
                        <button onClick={this.toggleDisplayBio}>Read more</button>
                    </div>
                ) 
                } */}
                <h6 class="nav-button">About Me</h6>
                <h6 class="nav-button">Code</h6>
                <h6 class="nav-button">Design</h6>
                <h6 class="nav-button">3D/Video</h6>
                <h6 class="nav-button">More</h6>

                <p>jesspeng@adobe.com</p>
                <span>
                    <img class="social-icon" src={insta} alt="Instagram"></img>
                    <img class="social-icon" src={linkedin} alt="Linkedin"></img>
                    <img class="social-icon" src={email} alt="Email"></img>
                </span>

            </div>
        ) 
    }
}
export default Sidepanel;
