import React from "react";
import { BrowserRouter as Router } from "react-router-dom";

import "react-tiger-transition/styles/main.min.css";
import "./index.scss";

import { Navigation, Route, Screen, Link, glide } from "react-tiger-transition";
import Home from './Home'; 
import Portfolio from './Portfolio'; 
import About from './About'; 
import Title from './Title'; 

// inject glide styles
glide({
  name: 'glide-left'
});
glide({
  name: 'glide-right',
  direction: 'right'
});

const screenStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center"
};

// you will need to set the height of  <Navigation /> wrapper,
// in this case, it is the root node,
// better to do this on your stylesheet
document.getElementById("root").style.height = "100vh";

export default () => (
  <Router>
    <Navigation>
      <Route exact path="/">
        <Screen
          style={{
            backgroundColor: "#e5a5eb",
            ...screenStyle
          }}
        >
          {/* Use Link the same way you use
                react-router Link, but
                add transition */}
          <Home></Home>
            <div className="container">
                <div className="center">
                  <Title></Title>
                </div>
                <div className="center">
                    <Link to="/portfolio" transition='glide-left'> <button className="home-btn portfolio">Portfolio</button></Link>
                    <Link to="/about" transition='glide-left'> <button className="home-btn about">About</button></Link>
                </div>
            </div>
        </Screen>
      </Route>
      <Route
        exact
        path="/portfolio"
        screen // shorthand to wrap children with screen
        screenProps={{
          style: {
            backgroundColor: "#607d88",
            ...screenStyle
          }
        }}
      >
        <Portfolio></Portfolio>
        <Link to="/" transition='glide-right'>
          Back to home page
        </Link>
      </Route>

      <Route
        exact
        path="/about"
        screen // shorthand to wrap children with screen
        screenProps={{
          style: {
            backgroundColor: "#607d8b",
            ...screenStyle
          }
        }}
      >
        <About></About>
        <Link to="/" transition='glide-right'>
          Back to home page
        </Link>
      </Route>
    </Navigation>
  </Router>
);
