/**
 *  basic css processor for tiger
 *  designed only for css written in tigers folder
 *
 */

const prefixes = [
  {
    name: 'transform',
    prefixes: ['-webkit-', '-ms-']
  },
  {
    name: 'transition',
    prefixes: ['-o-', '-webkit-']
