    0%, 25% {
      opacity: ${opacity};
      transform: ${transform0};
    }
    75% {
      opacity: ${opacity};
      transform: ${transform75};
    }
    100% {
      opacity: 1;
      transform: translateZ(0) translateX(0);
    }
  }
  `;

  return style;

};
