import React, { Component } from 'react'; 
import axios from 'axios'; 

class Contact extends Component {
    constructor(props){
        super(props);

        // on page load 
        this.state = {
            name: '', 
            email: '',
            message: ''
        }
    }

    onNameChange(event) {
        this.setState({ name : event.target.value }); 
    }

    onEmailChange(event) {
        this.setState({ email: event.target.value }); 
    }

    onMessageChange(event) {
        this.setState({ message: event.target.value }); 
    }

    handleSubmit(event) {
        event.preventDefault(); 

        axios({
            method: "POST",
            url: "http://localhost:3000/about",
            data: this.state
        }).then(response => {
            if (response.data.status === 'success') {
                alert("Message Sent.");
                this.resetForm();
            } else if (response.data.status === 'fail') {
                alert("Message failed to send."); 
            }
        })
    }

    resetForm() {
        this.setState({ name: '', email: '', message: '' }); 
    }

    render() {

        return(
            <div>
                <h1 class="h1 contact">any questions?</h1>
                <p>Feel free to send a message below!</p>
                <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                    <div className="form-group">
                        <label for="name">Name</label>
                        <input type="text" id="name" className="form-control" value={this.state.name} onChange={this.onNameChange.bind(this)}/>
                    </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" id="exampleInputEmail1" className="form-control" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)}/>
                    </div>
                    <div className="form-group">
                        <label for="message">Message</label>
                        <textarea id="message" className="form-control" rows="5" value={this.state.message} onChange={this.onMessageChange.bind(this)}></textarea>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        )                    
    }
}

export default Contact;
