import React, { Component } from 'react'; 
import { Slide } from 'react-slideshow-image';

import Syria from './assets/syriacover.jpg'; 
import Thai from './assets/thaicavecover.png';
import Crossword from './assets/nytcrossword.jpg'; 

class Portfolio extends Component {

    render() {
        const slideImages = [
            Syria, Thai, Crossword
        ];
        const properties = {
            duration: 5000,
            transitionDuration: 500,
            infinite: true,
            prevArrow: <div style={{width: "30px", marginRight: "-30px"}}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" fill="#fff"><path d="M242 180.6v-138L0 256l242 213.4V331.2h270V180.6z"/></svg></div>,
            nextArrow: <div style={{width: "30px", marginLeft: "-30px"}}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" fill="#fff"><path d="M512 256L270 42.6v138.2H0v150.6h270v138z"/></svg></div>
          };
        return (
        <div>
            <h1 className="h1 header">Portfolio</h1>
            <div className="container">
                <div className="row portfolio">
                    <p>1 of 5 <span><h2>Internship</h2></span></p>
                </div>
                <div className="row">
                    <div className="col-8">
                        {/* <div> */}
                        <Slide easing="ease">
                            <div className="each-slide">
                                <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                                {/* <span>Slide 1</span> */}
                                </div>
                            </div>
                            <div className="each-slide">
                                <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                                {/* <span>Slide 2</span> */}
                                </div>
                            </div>
                            <div className="each-slide">
                                <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
                                {/* <span>Slide 3</span> */}
                                </div>
                            </div>
                        </Slide>

                    {/* </div> */}
                    </div>
                    <div className="col-4">
                        <p className="p portfolio"><span><h3>The New York Times</h3></span>As a Graphics intern on the Immersive team, I 
                        prototyped 3-D assets to visualize AR moments, researched and tested the use of the Microsoft Hololens in future 
                        articles, and created cover images and diagrams as seen here and here. During the Maker's Week Hackathon, I also 
                        helped build the front end for our intern-led 3-D Crossword project.</p>
                    </div>
                </div>

                <div className="row">
                    <div className="col">


                        <p className="p portfolio">Inspired by old New Yorker cartoons, I started my own cartoon instagram called Peng and Ink as an outlet to showcase
                        my humor and creativity. It follows a potato-shaped, sassier, and much smaller version of me as I navigate the ups and downs of work, dating,
                        and everything in between. If you love cartoons or just want a laugh, feel free to give me a follow!:) 
                        </p>
                    </div>
                    <div className="col">
                    </div>
                </div>      
            </div>
        </div>
        )
    }
}

export default Portfolio;
