import React, { Component } from 'react'; 
import PengAndInk1 from './assets/pengandink.png';
import PengAndInk2 from './assets/comicstrip.png';

class AboutMe extends Component {

    render() {
        return (
          <div>
            <div class="content-container">
                <p>I am currently a Tech Consultant at Adobe in NYC. I majored in Digital Media Design and 
                    minored in Fine Arts at the University of Pennsylvania, where I graduated from the 
                    School of Engineering in May 2019.<br></br>
                    In my free time, I love cartooning (see below), doodling, watching stand up, and running. </p>
                <p>Inspired by old New Yorker cartoons, I decided to start my own cartoon instagram, Peng and Ink,
                    a year ago as an outlet to channel my humor and creativity. If you like cartoons or just want a laugh,
                    feel free to check it out!</p>
                <div class="row">
                <div class="col">
                <img src={PengAndInk1} alt="Peng and Ink" class="cropped"></img>
                </div>
                </div>
                <div class="row">
                <img src={PengAndInk2} alt="Cartoon" class="cropped"></img>
                </div>

            </div>
          </div>
        ) 
    }
}
export default AboutMe;
